import React,{useEffect,useState} from "react";
import UserActions from "./component/UserActions";
import CounterDisplay from "./component/CounterDisplay";
import Clock from "./component/Clock";
function App() {
    const [color, setColor] = React.useState(JSON.parse(sessionStorage.getItem('color')) || "blue");
    const [count, setCount] = React.useState(JSON.parse(localStorage.getItem('count')) || 0);

    console.log(color);
    console.log(count);

    useEffect(() => {
        sessionStorage.setItem('color', JSON.stringify(color));
        localStorage.setItem('count', JSON.stringify(count));

    }, [color, count]);
    return (
        <div>
            <DisplayMessage color={color}/>
            <Clock/>
            <CounterDisplay count ={count}/>
            <UserActions
                setColor={setColor}
                setCount={setCount}
                count={count}
                color={color}/>
        </div>
    );


}

function DisplayMessage(props) {
    return <h1 style={{color: props.color}}>Hello React World</h1>;
}




export default App;
